#include "diffie_hellman.h"

#include "mpz.h"

const auto p_value = mpz_class{
    ""
};

diffie_hellman::diffie_hellman()
    : p{p_value},
      g{2},
      x{random_prime(255)},
      y{powm(g, x, p)} {
}

mpz_class diffie_hellman::set_peer_public_key(const mpz_class &pub_key) {
    this->K = powm(pub_key, x, p);
    return K;
}
